select genes_from_ensembl.display_name,genes_from_ensembl.species, genes_from_ensembl.biotype,genes_from_uniprot.length AS length_of_protein, genes_from_ncbi.description as long_description, genes_from_ensembl.description as summary, genes_from_ncbi.start_position_on_the_genomic_accession as gene_start,genes_from_ncbi.end_position_on_the_genomic_accession as gene_end,genes_from_ncbi.gene_length, genes_from_ncbi.chromosome,genes_from_uniprot.sequence_start,genes_from_ncbi.orientation,genes_from_ncbi.exon_count from genes_from_ensembl LEFT JOIN genes_from_ncbi ON genes_from_ensembl.display_name = genes_from_ncbi.symbol LEFT JOIN genes_from_uniprot ON genes_from_ensembl.display_name LIKE substring_index((genes_from_uniprot.Gene),' ',1)\G;
